targets.query:
SELECT (sum("up") / sum("all")) * 100 AS "uptime" FROM (
SELECT "up", "all" FROM (
SELECT count("result_code") AS "all" FROM "ping" WHERE ("url" = 'edgerouter.example.com') AND $timeFilter GROUP BY "url"), (
SELECT count("result_code") AS "up" FROM "ping" WHERE ("url" = 'edgerouter.example.com') AND "result_code" = 0 AND $timeFilter GROUP BY "url"
)
)
